  1→import { useState, useRef } from 'react';
  2→import { X, Copy, Check, AlertCircle } from 'lucide-react';
  3→import { useSettings } from '../contexts/SettingsContext';
  4→import { useFocusTrap } from '../hooks/useFocusTrap';
  5→
  6→interface CopyPasteDialogProps {
  7→    prompt: string;
  8→    onSubmit: (response: string) => void;
  9→    onCancel: () => void;
 10→}
 11→
 12→type Step = 'copy' | 'paste';
 13→
 14→export const CopyPasteDialog: React.FC<CopyPasteDialogProps> = ({
 15→    prompt,
 16→    onSubmit,
 17→    onCancel,
 18→}) => {
 19→    const { t } = useSettings();
 20→    const [step, setStep] = useState<Step>('copy');
 21→    const [copied, setCopied] = useState(false);
 22→    const [response, setResponse] = useState('');
 23→    const [error, setError] = useState<string | null>(null);
 24→    const textareaRef = useRef<HTMLTextAreaElement>(null);
 25→    const dialogRef = useFocusTrap(onCancel);
 26→
 27→    const handleCopyAndProceed = async () => {
 28→        await navigator.clipboard.writeText(prompt);
 29→        setCopied(true);
 30→        // Advance to paste step after a brief moment to show "Copied!" feedback
 31→        setTimeout(() => {
 32→            setStep('paste');
 33→            setCopied(false);
 34→            setTimeout(() => textareaRef.current?.focus(), 100);
 35→        }, 600);
 36→    };
 37→
 38→    const handleSubmit = () => {
 39→        if (!response.trim()) {
 40→            setError(t.copyPaste.responseRequired);
 41→            return;
 42→        }
 43→        setError(null);
 44→        onSubmit(response);
 45→    };
 46→
 47→    return (
 48→        <div
 49→            className="fixed inset-0 z-[60] flex items-center justify-center p-4 bg-black/50 backdrop-blur-sm animate-in fade-in duration-200"
 50→            role="dialog"
 51→            aria-modal="true"
 52→            aria-labelledby="copy-paste-dialog-title"
 53→        >
 54→            <div
 55→                ref={dialogRef}
 56→                tabIndex={-1}
 57→                className="glass-panel w-full max-w-2xl max-h-[90vh] overflow-hidden flex flex-col shadow-2xl animate-in zoom-in-95 duration-200 outline-none"
 58→            >
 59→                {/* Header */}
 60→                <div className="flex items-center justify-between p-3 border-b border-border-base/30">
 61→                    <h2 id="copy-paste-dialog-title" className="text-xl font-bold bg-gradient-to-r from-primary to-secondary text-transparent bg-clip-text">
 62→                        {t.copyPaste.title}
 63→                    </h2>
 64→                    <button
 65→                        onClick={onCancel}
 66→                        className="p-2 bg-white/50 hover:bg-white/50 dark:bg-black/20 dark:hover:bg-black/30 rounded-full transition-colors text-text-muted hover:text-text-base focus:outline-none focus:ring-2 focus:ring-primary"
 67→                        aria-label="Close"
 68→                    >
 69→                        <X size={20} />
 70→                    </button>
 71→                </div>
 72→
 73→                {/* Content */}
 74→                <div className="flex-1 overflow-y-auto p-3 min-h-0 flex flex-col gap-2">
 75→                    {step === 'copy' ? (
 76→                        <>
 77→                            <div className="flex items-start gap-2 text-sm text-text-muted bg-blue-500/10 rounded-lg p-3">
 78→                                <AlertCircle size={16} className="text-blue-500 shrink-0 mt-0.5" />
 79→                                <span>
 80→                                    {t.copyPaste.instructions}{' '}
 81→                                    <Copy size={14} className="inline-block align-text-bottom mx-0.5" />
 82→                                    {' '}{t.copyPaste.instructionsEnd}
 83→                                </span>
 84→                            </div>
 85→                            <div className="bg-white/30 dark:bg-black/20 rounded-lg p-3 flex-1 overflow-y-auto">
 86→                                <pre className="text-xs text-text-base whitespace-pre-wrap font-mono">
 87→                                    {prompt}
 88→                                </pre>
 89→                            </div>
 90→                        </>
 91→                    ) : (
 92→                        <>
 93→                            <div className="flex items-start gap-2 text-sm text-text-muted bg-blue-500/10 rounded-lg p-3">
 94→                                <AlertCircle size={16} className="text-blue-500 shrink-0 mt-0.5" />
 95→                                <span>
 96→                                    {t.copyPaste.instructionsPaste}{' '}
 97→                                    <Copy size={14} className="inline-block align-text-bottom mx-0.5" />
 98→                                    {' '}{t.copyPaste.instructionsPasteEnd}
 99→                                </span>
100→                            </div>
101→                            <textarea
102→                                ref={textareaRef}
103→                                value={response}
104→                                onChange={(e) => {
105→                                    setResponse(e.target.value);
106→                                    setError(null);
107→                                }}
108→                                placeholder={t.copyPaste.responsePlaceholder}
109→                                className="w-full flex-1 bg-white/30 dark:bg-black/20 rounded-lg p-3 text-sm font-mono resize-none focus:outline-none focus:ring-2 focus:ring-primary/50"
110→                            />
111→                            {error && (
112→                                <div
113→                                    role="alert"
114→                                    aria-live="assertive"
115→                                    className="flex items-center gap-2 text-sm text-red-500"
116→                                >
117→                                    <AlertCircle size={16} />
118→                                    <span>{error}</span>
119→                                </div>
120→                            )}
121→                        </>
122→                    )}
123→                </div>
124→
125→                {/* Footer */}
126→                <div className="flex items-center justify-end gap-3 p-3 border-t border-border-base/30">
127→                    {step === 'copy' ? (
128→                        <button
129→                            onClick={handleCopyAndProceed}
130→                            disabled={copied}
131→                            autoFocus
132→                            className={`btn btn-primary flex items-center justify-center gap-2 px-6 py-2.5 rounded-lg min-w-[160px] transition-colors ${
133→                                copied ? '!bg-green-500' : ''
134→                            }`}
135→                        >
136→                            {copied ? (
137→                                <>
138→                                    <Check size={18} />
139→                                    {t.copyPaste.copied}
140→                                </>
141→                            ) : (
142→                                <>
143→                                    <Copy size={18} />
144→                                    {t.copyPaste.copyPrompt}
145→                                </>
146→                            )}
147→                        </button>
148→                    ) : (
149→                        <>
150→                            <button
151→                                onClick={() => setStep('copy')}
152→                                className="btn flex items-center gap-2 px-4 py-2 rounded-lg bg-white/50 dark:bg-black/20 hover:bg-white/70 dark:hover:bg-black/30"
153→                            >
154→                                {t.copyPaste.back}
155→                            </button>
156→                            <button
157→                                onClick={handleSubmit}
158→                                className="btn btn-primary flex items-center gap-2 px-4 py-2 rounded-lg"
