    defaultProps.onSubmit = vi.fn();
    defaultProps.onCancel = vi.fn();
    mockWriteText.mockResolvedValue(undefined);
    Object.defineProperty(navigator, 'clipboard', {
        value: { writeText: mockWriteText },
        writable: true,
        configurable: true,
    });
});

