/**
 * Core type definitions for the Recipe Planner application.
 */

/**
 * An item in the user's pantry inventory.
 */
export interface PantryItem {
  id: string;
  name: string;
  amount: string;
}

/**
 * An ingredient used in a recipe or shopping list.
 */
export interface Ingredient {
  item: string;
  amount: string;
  unit?: string;
}

/**
 * Estimated nutritional information per serving.
 * Values are rough estimates from the LLM.
 */
export interface Nutrition {
  calories: number;
  carbs: number;    // grams
  fat: number;      // grams
  protein: number;  // grams
}

/**
 * A generated recipe with ingredients and instructions.
 */
export interface Recipe {
  id: string;
  title: string;
  time: string;
  ingredients: Ingredient[];
  instructions: string[];
  usedIngredients: string[]; // List of PantryItem IDs used
  missingIngredients?: Ingredient[]; // Optional: excluded in shared recipes
  nutrition?: Nutrition; // Optional for backwards compatibility
  comments?: string; // Optional fun fact or remark from the LLM
}

/**
 * A complete meal plan generated by the LLM.
 */
export interface MealPlan {
  recipes: Recipe[];
  shoppingList: Ingredient[];
}

/**
 * A notification displayed to the user (error or undo message).
 */
export interface Notification {
  message: string;
  type: 'error' | 'undo';
  action?: {
    label: string;
    onClick: () => void;
    ariaLabel?: string; // Contextual aria-label for screen readers
  };
  timeout?: number; // Auto-dismiss after X milliseconds
}

/**
 * Centralized constants for the application.
 * Prevents magic strings scattered throughout the codebase.
 */

/**
 * LocalStorage keys used for persisting user data and preferences.
 */
export const STORAGE_KEYS = {
    API_KEY: 'gemini_api_key',
    USE_COPY_PASTE: 'use_copy_paste',
    API_KEY_WARNING_SEEN: 'api_key_warning_seen',
    SPICE_RACK: 'spice_rack_items',
    PEOPLE_COUNT: 'people_count',
    MEALS_COUNT: 'meals_count',
    DIET_PREFERENCE: 'diet_preference',
    STYLE_WISHES: 'style_wishes',
    LANGUAGE: 'language',
    HEADER_MINIMIZED: 'header_minimized',
    OPTIONS_MINIMIZED: 'options_minimized',
    PANTRY_MINIMIZED: 'pantry_minimized',
    SPICE_RACK_MINIMIZED: 'spice_rack_minimized',
    SHOPPING_LIST_MINIMIZED: 'shopping_list_minimized',
    RECIPE_MISSING_INGREDIENTS_MINIMIZED: 'recipe_missing_ingredients_minimized',
    SHOPPING_LIST_CHECKED: 'shopping_list_checked',
    SHOPPING_LIST_CHECKED_SHARED: 'shopping_list_checked_shared',
    MEAL_PLAN: 'meal_plan',
    WELCOME_DISMISSED: 'welcome_dismissed',
    PANTRY_ITEMS: 'pantry_items',
} as const;

/**
 * URL query parameter names for sharing functionality.
 */
export const URL_PARAMS = {
    RECIPE: 'recipe',
    SHOPPING_LIST: 'shoppingList',
} as const;

/**
 * API configuration for the LLM service.
 */
export const API_CONFIG = {
    BASE_URL: 'https://generativelanguage.googleapis.com/v1beta/models',
    MODEL: 'gemini-3-flash-preview',
    TIMEOUT_MS: 60000,
    KEY_URL: 'https://aistudio.google.com/app/apikey',
} as const;

/**
 * Default values for user settings.
 */
export const DEFAULTS = {
    PEOPLE_COUNT: 2,
    MEALS_COUNT: 3,
    DIET: 'Flexitarian',
    LANGUAGE: 'English',
} as const;

/**
 * Input validation constraints.
 */
export const VALIDATION = {
    MAX_INPUT_LENGTH: 200,
} as const;

